var data = require('./data.json');
window.THREE = require('three');
var EffectComposer = require('three-effectcomposer')(THREE);

var Controller = require('./Controller');
var Shaders = require('./Shaders');
var Body = require('./Body');

var Main = function( ) {
	this.node = document.getElementById('main');

	this.scene = new THREE.Scene();
	this.camera = new THREE.OrthographicCamera();
	this.renderer = new THREE.WebGLRenderer( { alpha : false, antialias : true } );
	this.node.appendChild( this.renderer.domElement );

	var bpms = 60 / data[ 0 ].bpm * 1000;
	this.controller = new Controller( bpms );

	this.body = new Body( this.controller, { fontSize : 13, word : data[0].song } );
	this.scene.add( this.body );

	// Create your composer and first RenderPass
	var renderTarget = new THREE.WebGLRenderTarget( this.node.offsetWidth * 2, this.node.offsetHeight * 2, { depthBuffer : false, stencilBuffer : false } );
	this.composer = new EffectComposer( this.renderer, renderTarget );
	this.composer.addPass(new EffectComposer.RenderPass( this.scene, this.camera ) );


	var txtr = new THREE.TextureLoader().load( 'data:image/png;base64,' );
	// And another shader, drawing to the screen at this point

	this.effect = new EffectComposer.ShaderPass( Shaders.invert );
	this.composer.addPass( this.effect );

	// this.effect2 = new EffectComposer.ShaderPass( Shaders.RGBShift );
	// this.composer.addPass( this.effect2 );

	// this.effect3 = new EffectComposer.ShaderPass( Shaders.glitch );
	// this.composer.addPass( this.effect3 );

	// this.effect4 = new EffectComposer.ShaderPass( Shaders.cromaticAberration );
	// this.composer.addPass( this.effect4 );

	// this.effect5 = new EffectComposer.ShaderPass( Shaders.mpeg );
	// this.effect5.uniforms.noise.value = txtr;
	// this.composer.addPass( this.effect5 );

	this.noiseShader = new EffectComposer.ShaderPass( Shaders.noise );
	this.composer.addPass( this.noiseShader );

	this.noiseShader.renderToScreen = true;

	console.log()

	window.addEventListener( 'keydown', this.keyDown.bind( this ) );
	window.addEventListener( 'resize', this.resize.bind( this ) );

	this.resize();
	this.step();
}

Main.prototype.keyDown = function( e ){
	if( e.keyCode == 37 ) this.body.prevWord(); // left
	if( e.keyCode == 39 ) this.body.nextWord(); // right
}

Main.prototype.resize = function( e ) {
	var width = this.node.offsetWidth, height = this.node.offsetHeight;

	var camView = { left :  width / -2, right : width / 2, top : height / 2, bottom : height / -2 };
	for ( var prop in camView) this.camera[ prop ] = camView[ prop ];
	this.camera.position.z = 1000;	
	this.renderer.setSize( width * 2, height * 2 );
	this.renderer.domElement.setAttribute( 'style', 'width:' + width + 'px; height:' + height + 'px;' );
	this.camera.updateProjectionMatrix( );

	this.body.resize( width, height );
}

Main.prototype.step = function( time ) {
	window.requestAnimationFrame( this.step.bind( this ) );

	// this.effect2.uniforms.amount.value = this.controller.beatPeak / 100;
	// this.effect2.uniforms.angle.value = this.controller.noiseMid;

	// this.effect3.uniforms.time.value = 1200 + Math.sin(time) * 1000;

	// this.effect4.uniforms.time.value = time;

	// this.effect5.uniforms.time.value = 1200 + Math.sin(time) * 1000;

	this.noiseShader.uniforms.time.value = this.controller.random;

	this.body.step( time , this.renderer );
	this.controller.step( time );
	this.composer.render()
};

var root = new Main();